import React, { useState, useEffect } from 'react';
import AutoComplete from './AutoComplete';
import { vehiclesService } from '../services/vehiclesService';

const ItemForm = ({ onAddItem }) => {
  const [itemName, setItemName] = useState('');
  const [selectedMaterial, setSelectedMaterial] = useState(null);
  const [quantity, setQuantity] = useState('');
  const [vehicle, setVehicle] = useState('');
  const [unitPrice, setUnitPrice] = useState('');
  const [vehicles, setVehicles] = useState([]);
  const [loadingVehicles, setLoadingVehicles] = useState(true);

  // Carregar veículos ao montar o componente
  useEffect(() => {
    loadVehicles();
  }, []);

  const loadVehicles = async () => {
    try {
      const result = await vehiclesService.getAllVehicles();
      if (result.success) {
        setVehicles(result.data);
      }
    } catch (error) {
      console.error('Erro ao carregar veículos:', error);
    } finally {
      setLoadingVehicles(false);
    }
  };

  const handleMaterialSelect = (material) => {
    setSelectedMaterial(material);
    setQuantity('');
    setVehicle('');
  };

  const handleAddItem = () => {
    if (!selectedMaterial || !quantity || !vehicle || !unitPrice) {
      alert('Por favor, preencha todos os campos obrigatórios');
      return;
    }

    const selectedVehicle = vehicles.find(v => v.id === parseInt(vehicle));
    
    const item = {
      id: Date.now(),
      materialId: selectedMaterial.id,
      materialName: selectedMaterial.nome,
      quantity: parseFloat(quantity),
      unit: selectedMaterial.tipo_medida,
      vehicleId: parseInt(vehicle),
      vehicleName: selectedVehicle?.nome,
      unitPrice: parseFloat(unitPrice),
      totalPrice: parseFloat(quantity) * parseFloat(unitPrice)
    };

    onAddItem(item);
    
    // Reset form
    setItemName('');
    setSelectedMaterial(null);
    setQuantity('');
    setVehicle('');
    setUnitPrice('');
  };

  const getAvailableVehicles = () => {
    if (!selectedMaterial) return [];
    
    // Todos os veículos disponíveis para o material
    return selectedMaterial.vehicles;
  };

  return (
    <div className="form-section">
      <h3>Adicionar Item ao Pedido</h3>
      
      <div className="form-grid">
        <div className="form-group">
          <label>Material</label>
          <AutoComplete
            value={itemName}
            onChange={setItemName}
            suggestions={materials}
            placeholder="Digite o nome do material (ex: areia, cano, joelho...)"
            onSelect={handleMaterialSelect}
            filterFunction={(item, value) => 
              item.name.toLowerCase().includes(value.toLowerCase()) ||
              item.category.toLowerCase().includes(value.toLowerCase())
            }
          />
        </div>

        {/* Tipo de Medida - Selecionável */}
        {selectedMaterial && (
          <div className="form-group">
            <label>Tipo de Medida *</label>
            <select
              value={measureType}
              onChange={(e) => setMeasureType(e.target.value)}
            >
              <option value="">Selecione o tipo</option>
              <option value="metro">Metro</option>
              <option value="quantidade">Quantidade</option>
            </select>
          </div>
        )}

        {/* Campo Quantidade - aparece quando tipo de medida for selecionado */}
        {measureType && (
          <div className="form-group">
            <label>
              {measureType === 'metro' ? 'Metros *' : 'Quantidade *'}
            </label>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              placeholder={measureType === 'metro' ? "Ex: 2.5, 10, 0.5" : "Ex: 5, 10, 20"}
              step="0.1"
              min="0"
            />
            <small style={{ color: '#666', marginTop: '4px' }}>
              {measureType === 'metro' ? 'Unidade: metro' : `Unidade: ${selectedMaterial.unit}`}
            </small>
          </div>
        )}

        <div className="form-group">
          <label>Veículo *</label>
          <select
            value={vehicle}
            onChange={(e) => setVehicle(e.target.value)}
            disabled={!selectedMaterial}
          >
            <option value="">Selecione o veículo</option>
            {getAvailableVehicles().map(v => (
              <option key={v} value={v}>
                {vehicleInfo[v].name} - {vehicleInfo[v].capacity}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Preço Unitário (R$) *</label>
          <input
            type="number"
            value={unitPrice}
            onChange={(e) => setUnitPrice(e.target.value)}
            placeholder="0.00"
            step="0.01"
            min="0"
          />
          {selectedMaterial && measureType && (
            <small style={{ color: '#666', marginTop: '4px' }}>
              Preço por {measureType === 'metro' ? 'metro' : selectedMaterial.unit}
            </small>
          )}
        </div>
      </div>

      <button 
        className="btn btn-primary" 
        onClick={handleAddItem}
        disabled={
          !selectedMaterial || 
          !measureType ||
          !quantity ||
          !vehicle || 
          !unitPrice
        }
      >
        Adicionar ao Pedido
      </button>
    </div>
  );
};

export default ItemForm;
