import React, { useState, useEffect } from "react";
import { orderService } from "../services/orderService";
import { Package, User, MapPin, Phone, Calendar } from "lucide-react";

const OrdersList = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    setLoading(true);
    try {
      const result = await orderService.getOrders();
      if (result.success) {
        setOrders(result.data);
        setError(null);
      } else {
        setError(result.error);
      }
    } catch (err) {
      setError("Erro ao carregar pedidos");
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat("pt-BR", {
      style: "currency",
      currency: "BRL",
    }).format(value);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString("pt-BR");
  };

  const formatAddress = (order) => {
    // Como o endereço agora está em campos separados
    const parts = [];

    if (order.rua) parts.push(order.rua);
    if (order.numero) parts.push(`nº ${order.numero}`);
    if (order.bairro) parts.push(order.bairro);
    if (order.cidade) parts.push(order.cidade);

    return parts.join(", ");
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "pendente":
        return "#f59e0b"; // Amarelo vibrante para pendente
      case "entregue":
        return "#059669"; // Verde mais forte para entregue/concluído
      case "cancelado":
        return "#dc2626"; // Vermelho mais forte para cancelado
      default:
        return "#f59e0b"; // Amarelo como padrão (pendente)
    }
  };

  const getStatusBackground = (status) => {
    switch (status) {
      case "pendente":
        return "#fef3c7"; // Fundo amarelo claro
      case "entregue":
        return "#d1fae5"; // Fundo verde claro
      case "cancelado":
        return "#fee2e2"; // Fundo vermelho claro
      default:
        return "#fef3c7"; // Fundo amarelo claro como padrão
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case "pendente":
        return "Pendente";
      case "entregue":
        return "Entregue";
      case "cancelado":
        return "Cancelado";
      default:
        return status || "Pendente";
    }
  };

  const updateStatus = async (orderId, newStatus) => {
    try {
      const result = await orderService.updateOrderStatus(orderId, newStatus);
      if (result.success) {
        loadOrders(); // Recarregar lista
      } else {
        alert("Erro ao atualizar status: " + result.error);
      }
    } catch (err) {
      alert("Erro ao atualizar status");
    }
  };

  if (loading) {
    return (
      <div className="form-section">
        <h3>Pedidos Registrados</h3>
        <p style={{ textAlign: "center", padding: "20px" }}>
          Carregando pedidos...
        </p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="form-section">
        <h3>Pedidos Registrados</h3>
        <p style={{ textAlign: "center", padding: "20px", color: "#ef4444" }}>
          Erro: {error}
        </p>
        <button className="btn btn-primary" onClick={loadOrders}>
          Tentar Novamente
        </button>
      </div>
    );
  }

  return (
    <div className="form-section">
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "20px",
        }}
      >
        <h3>Pedidos Registrados ({orders.length})</h3>
        <button className="btn btn-primary" onClick={loadOrders}>
          Atualizar
        </button>
      </div>

      {orders.length === 0 ? (
        <p style={{ textAlign: "center", padding: "20px", color: "#666" }}>
          Nenhum pedido encontrado.
        </p>
      ) : (
        <div style={{ display: "grid", gap: "15px" }}>
          {orders.map((order) => (
            <div
              key={order.id}
              style={{
                border: "1px solid #e5e7eb",
                borderRadius: "8px",
                padding: "15px",
                backgroundColor: "#fff",
              }}
            >
              {/* Cabeçalho do pedido */}
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  marginBottom: "15px",
                  paddingBottom: "10px",
                  borderBottom: "1px solid #f3f4f6",
                }}
              >
                <div>
                  <h4 style={{ margin: 0 }}>Pedido #{order.id}</h4>
                  <div
                    style={{
                      fontSize: "12px",
                      color: "#666",
                      display: "flex",
                      alignItems: "center",
                      gap: "5px",
                    }}
                  >
                    <Calendar size={12} />
                    {formatDate(order.created_at)}
                  </div>
                </div>
                <div
                  style={{ display: "flex", alignItems: "center", gap: "10px" }}
                >
                  <span
                    style={{
                      padding: "6px 12px",
                      borderRadius: "6px",
                      fontSize: "12px",
                      fontWeight: "bold",
                      backgroundColor: getStatusBackground(order.status),
                      color: getStatusColor(order.status),
                      border: `1px solid ${getStatusColor(order.status)}30`,
                    }}
                  >
                    {getStatusText(order.status)}
                  </span>
                  <span
                    style={{
                      fontSize: "18px",
                      fontWeight: "bold",
                      color: "#059669",
                    }}
                  >
                    {formatCurrency(order.total_pedido)}
                  </span>
                </div>
              </div>

              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
                  gap: "15px",
                }}
              >
                {/* Dados do Cliente */}
                <div>
                  <h5
                    style={{
                      margin: "0 0 8px 0",
                      display: "flex",
                      alignItems: "center",
                      gap: "5px",
                    }}
                  >
                    <User size={16} />
                    Cliente
                  </h5>
                  <div style={{ fontSize: "14px", color: "#374151" }}>
                    <div>
                      <strong>{order.cliente_nome}</strong>
                    </div>
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                        gap: "5px",
                      }}
                    >
                      <Phone size={12} />
                      {order.telefone}
                    </div>
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                        gap: "5px",
                      }}
                    >
                      <MapPin size={12} />
                      {formatAddress(order)}
                    </div>
                    {order.observacoes && (
                      <div style={{ marginTop: "5px", fontStyle: "italic" }}>
                        "{order.observacoes}"
                      </div>
                    )}
                  </div>
                </div>

                {/* Itens do Pedido */}
                <div>
                  <h5
                    style={{
                      margin: "0 0 8px 0",
                      display: "flex",
                      alignItems: "center",
                      gap: "5px",
                    }}
                  >
                    <Package size={16} />
                    Itens ({order.pedido_itens?.length || 0})
                  </h5>
                  <div
                    style={{
                      fontSize: "14px",
                      maxHeight: "120px",
                      overflowY: "auto",
                    }}
                  >
                    {order.pedido_itens?.map((item, index) => (
                      <div
                        key={index}
                        style={{
                          padding: "5px 0",
                          borderBottom:
                            index < order.pedido_itens.length - 1
                              ? "1px solid #f3f4f6"
                              : "none",
                        }}
                      >
                        <div
                          style={{
                            display: "flex",
                            justifyContent: "space-between",
                          }}
                        >
                          <span>
                            <strong>
                              {item.materiais?.nome || "Material"}
                            </strong>
                          </span>
                          <span>{formatCurrency(item.total_item)}</span>
                        </div>
                        <div style={{ fontSize: "12px", color: "#666" }}>
                          {item.quantidade}{" "}
                          {item.materiais?.tipo_medida || "unidade"} -{" "}
                          {item.tipo_medida_usado === "metro"
                            ? "Por metro"
                            : "Por quantidade"}
                          <span
                            style={{
                              marginLeft: "8px",
                              padding: "2px 6px",
                              fontSize: "10px",
                              backgroundColor: "#dbeafe",
                              color: "#1e40af",
                              borderRadius: "4px",
                            }}
                          >
                            {item.veiculos?.nome || "Veículo"}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {/* Ações */}
              {order.status === "pendente" && (
                <div
                  style={{
                    marginTop: "15px",
                    paddingTop: "15px",
                    borderTop: "1px solid #f3f4f6",
                    display: "flex",
                    gap: "10px",
                  }}
                >
                  <button
                    className="btn btn-success"
                    onClick={() => updateStatus(order.id, "entregue")}
                    style={{ fontSize: "12px", padding: "6px 12px" }}
                  >
                    Marcar como Entregue
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => updateStatus(order.id, "cancelado")}
                    style={{ fontSize: "12px", padding: "6px 12px" }}
                  >
                    Cancelar
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default OrdersList;
