import React, { useState } from 'react';
import AutoComplete from './AutoComplete';
import { materials, vehicleInfo } from '../data/materials';

const ItemForm = ({ onAddItem }) => {
  const [itemName, setItemName] = useState('');
  const [selectedMaterial, setSelectedMaterial] = useState(null);
  const [measureType, setMeasureType] = useState(''); // 'quantity' ou 'size'
  const [quantity, setQuantity] = useState('');
  const [size, setSize] = useState('');
  const [vehicle, setVehicle] = useState('');
  const [unitPrice, setUnitPrice] = useState('');

  const handleMaterialSelect = (material) => {
    setSelectedMaterial(material);
    setMeasureType(''); // Resetar para o usuário escolher
    setQuantity('');
    setSize('');
    setVehicle('');
  };

  const handleAddItem = () => {
    if (!selectedMaterial || !measureType || !vehicle || !unitPrice || !quantity) {
      alert('Por favor, preencha todos os campos obrigatórios');
      return;
    }

    // Determinar a unidade baseada no tipo de medida escolhido
    let unit = selectedMaterial.unit;
    if (measureType === 'metro') {
      unit = 'metro';
    } else if (measureType === 'quantidade') {
      unit = selectedMaterial.unit;
    }

    const item = {
      id: Date.now(),
      name: selectedMaterial.name,
      quantity: parseFloat(quantity),
      unit: unit,
      size: '-', // Não usamos mais tamanho P/M/G
      vehicle,
      unitPrice: parseFloat(unitPrice),
      totalPrice: parseFloat(quantity) * parseFloat(unitPrice),
      category: selectedMaterial.category,
      measureType: measureType
    };

    onAddItem(item);
    
    // Reset form
    setItemName('');
    setSelectedMaterial(null);
    setMeasureType('');
    setQuantity('');
    setSize('');
    setVehicle('');
    setUnitPrice('');
  };

  const getAvailableVehicles = () => {
    if (!selectedMaterial) return [];
    
    // Para itens por quantidade, usar os veículos do material
    if (measureType === 'quantity') {
      return selectedMaterial.vehicles;
    }
    
    // Para itens por tamanho, filtrar baseado no tamanho selecionado
    if (measureType === 'size' && size) {
      return selectedMaterial.vehicles.filter(vehicleType => {
        if (size === 'P') return true;
        if (size === 'M') return vehicleType !== 'moto';
        if (size === 'G') return vehicleType === 'caminhao' || vehicleType === 'carro';
        return true;
      });
    }
    
    return [];
  };

  return (
    <div className="form-section">
      <h3>Adicionar Item ao Pedido</h3>
      
      <div className="form-grid">
        <div className="form-group">
          <label>Material</label>
          <AutoComplete
            value={itemName}
            onChange={setItemName}
            suggestions={materials}
            placeholder="Digite o nome do material (ex: areia, cano, joelho...)"
            onSelect={handleMaterialSelect}
            filterFunction={(item, value) => 
              item.name.toLowerCase().includes(value.toLowerCase()) ||
              item.category.toLowerCase().includes(value.toLowerCase())
            }
          />
        </div>

        {/* Tipo de Medida */}
        {selectedMaterial && (
          <div className="form-group">
            <label>Tipo de Medida</label>
            <div style={{ 
              padding: '10px', 
              backgroundColor: '#f3f4f6', 
              borderRadius: '8px',
              fontSize: '14px',
              fontWeight: '600'
            }}>
              {measureType === 'quantity' ? 
                `Por Quantidade (${selectedMaterial.unit})` : 
                'Por Tamanho (P/M/G)'
              }
            </div>
          </div>
        )}

        {/* Campo Quantidade - só aparece se for tipo 'quantity' */}
        {measureType === 'quantity' && (
          <div className="form-group">
            <label>Quantidade *</label>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              placeholder="Ex: 5, 0.5, 2.5"
              step="0.1"
              min="0"
            />
            <small style={{ color: '#666', marginTop: '4px' }}>
              Unidade: {selectedMaterial.unit}
            </small>
          </div>
        )}

        {/* Campo Tamanho - só aparece se for tipo 'size' */}
        {measureType === 'size' && (
          <div className="form-group">
            <label>Tamanho *</label>
            <select
              value={size}
              onChange={(e) => setSize(e.target.value)}
            >
              <option value="">Selecione o tamanho</option>
              {selectedMaterial?.sizes.map(s => (
                <option key={s} value={s}>
                  {s} - {s === 'P' ? 'Pequeno' : s === 'M' ? 'Médio' : 'Grande'}
                </option>
              ))}
            </select>
            <small style={{ color: '#666', marginTop: '4px' }}>
              Unidade: {selectedMaterial.unit}
            </small>
          </div>
        )}

        <div className="form-group">
          <label>Veículo *</label>
          <select
            value={vehicle}
            onChange={(e) => setVehicle(e.target.value)}
            disabled={!selectedMaterial || (measureType === 'size' && !size)}
          >
            <option value="">Selecione o veículo</option>
            {getAvailableVehicles().map(v => (
              <option key={v} value={v}>
                {vehicleInfo[v].name} - {vehicleInfo[v].capacity}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Preço Unitário (R$) *</label>
          <input
            type="number"
            value={unitPrice}
            onChange={(e) => setUnitPrice(e.target.value)}
            placeholder="0.00"
            step="0.01"
            min="0"
          />
          {selectedMaterial && (
            <small style={{ color: '#666', marginTop: '4px' }}>
              Preço por {selectedMaterial.unit}
            </small>
          )}
        </div>
      </div>

      <button 
        className="btn btn-primary" 
        onClick={handleAddItem}
        disabled={
          !selectedMaterial || 
          !vehicle || 
          !unitPrice || 
          (measureType === 'quantity' && !quantity) ||
          (measureType === 'size' && !size)
        }
      >
        Adicionar ao Pedido
      </button>
    </div>
  );
};

export default ItemForm;
