import React, { useState } from 'react';
import CustomerForm from './components/CustomerForm';
import ItemForm from './components/ItemForm';
import ItemsList from './components/ItemsList';
import DeliverySummary from './components/DeliverySummary';
import { Building2, Package } from 'lucide-react';

function App() {
  const [customer, setCustomer] = useState({
    name: '',
    phone: '',
    address: {
      city: '',
      street: '',
      number: '',
      neighborhood: ''
    },
    notes: ''
  });

  const [items, setItems] = useState([]);

  const handleAddItem = (item) => {
    setItems(prevItems => [...prevItems, item]);
  };

  const handleRemoveItem = (itemId) => {
    setItems(prevItems => prevItems.filter(item => item.id !== itemId));
  };

  const handleFinalize = () => {
    const orderData = {
      customer,
      items,
      total: items.reduce((sum, item) => sum + item.totalPrice, 0),
      createdAt: new Date().toISOString(),
      id: Date.now()
    };

    // Formatar endereço para exibição
    const formatAddress = (address) => {
      if (!address) return '';
      if (typeof address === 'string') return address;
      
      const { street, number, neighborhood, city } = address;
      const parts = [];
      
      if (street) parts.push(street);
      if (number) parts.push(`nº ${number}`);
      if (neighborhood) parts.push(neighborhood);
      if (city) parts.push(city);
      
      return parts.join(', ');
    };

    // Aqui você pode integrar com Supabase ou outro backend
    console.log('Pedido finalizado:', orderData);
    
    // Simular salvamento
    alert(`Pedido finalizado com sucesso!\n\nCliente: ${customer.name}\nEndereço: ${formatAddress(customer.address)}\nItens: ${items.length}\nTotal: R$ ${orderData.total.toFixed(2)}\n\nO pedido foi registrado no sistema.`);
    
    // Limpar formulário após finalizar
    handleClear();
  };

  const handleClear = () => {
    setCustomer({
      name: '',
      phone: '',
      address: {
        city: '',
        street: '',
        number: '',
        neighborhood: ''
      },
      notes: ''
    });
    setItems([]);
  };

  return (
    <div className="container">
      {/* Header */}
      <div className="header">
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '12px' }}>
          <Building2 size={32} />
          <div>
            <h1 style={{ margin: 0, fontSize: '28px' }}>GH Construção</h1>
            <p style={{ margin: 0, opacity: 0.9 }}>Painel de Entregas</p>
          </div>
        </div>
      </div>

      {/* Formulário do Cliente */}
      <CustomerForm 
        customer={customer} 
        onChange={setCustomer} 
      />

      {/* Formulário de Itens */}
      <ItemForm onAddItem={handleAddItem} />

      {/* Lista de Itens */}
      <ItemsList 
        items={items} 
        onRemoveItem={handleRemoveItem} 
      />

      {/* Resumo da Entrega */}
      <DeliverySummary 
        customer={customer}
        items={items}
        onFinalize={handleFinalize}
        onClear={handleClear}
      />

      {/* Footer */}
      <div style={{ 
        textAlign: 'center', 
        padding: '20px', 
        color: '#666', 
        fontSize: '14px',
        marginTop: '30px'
      }}>
        <Package size={16} style={{ verticalAlign: 'middle', marginRight: '5px' }} />
        Sistema de Entregas - GH Construção © 2024
      </div>
    </div>
  );
}

export default App;
