import React, { useState } from 'react';
import './index.css';

function App() {
  const [message, setMessage] = useState('Sistema carregando...');

  React.useEffect(() => {
    setMessage('GH Construção - Sistema Online!');
  }, []);

  return (
    <div className="container">
      {/* Header */}
      <div className="header">
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', width: '100%' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
            <Building2 size={32} />
            <div>
              <h1 style={{ margin: 0, fontSize: '28px' }}>GH Construção</h1>
              <p style={{ margin: 0, opacity: 0.9 }}>Painel de Entregas</p>
            </div>
          </div>
          
          {/* Navegação */}
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            <button 
              className={`btn ${currentView === 'new-order' ? 'btn-success' : 'btn-primary'}`}
              onClick={() => setCurrentView('new-order')}
              style={{ display: 'flex', alignItems: 'center', gap: '5px' }}
            >
              <Plus size={16} />
              Novo Pedido
            </button>
            <button 
              className={`btn ${currentView === 'orders-list' ? 'btn-success' : 'btn-primary'}`}
              onClick={() => setCurrentView('orders-list')}
              style={{ display: 'flex', alignItems: 'center', gap: '5px' }}
            >
              <List size={16} />
              Ver Pedidos
            </button>
            <button 
              className={`btn ${currentView === 'delivery-by-vehicle' ? 'btn-success' : 'btn-primary'}`}
              onClick={() => setCurrentView('delivery-by-vehicle')}
              style={{ display: 'flex', alignItems: 'center', gap: '5px' }}
            >
              <Truck size={16} />
              Entregas
            </button>
          </div>
        </div>
      </div>

      {/* Conteúdo baseado na view atual */}
      {currentView === 'new-order' && (
        <>
          {/* Formulário do Cliente */}
          <CustomerForm 
            customer={customer} 
            onChange={setCustomer} 
          />

          {/* Formulário de Itens */}
          <ItemForm onAddItem={handleAddItem} />

          {/* Lista de Itens */}
          <ItemsList 
            items={items} 
            onRemoveItem={handleRemoveItem} 
          />

          {/* Resumo da Entrega */}
          <DeliverySummary 
            customer={customer}
            items={items}
            onFinalize={handleFinalize}
            onClear={handleClear}
            isLoading={isLoading}
          />
        </>
      )}

      {currentView === 'orders-list' && (
        <OrdersList />
      )}

      {currentView === 'delivery-by-vehicle' && (
        <DeliveryByVehicle />
      )}

      {/* Footer */}
      <div style={{ 
        textAlign: 'center', 
        padding: '20px', 
        color: '#666', 
        fontSize: '14px',
        marginTop: '30px'
      }}>
        <Package size={16} style={{ verticalAlign: 'middle', marginRight: '5px' }} />
        Sistema de Entregas - GH Construção © 2024
      </div>
    </div>
  );
}

export default App;
